<?php
	// multipart UCS-2 hex encoded SMS to UTF-8 string
	
	$text = "";
	
	$text = preg_replace("/05000......./", "", $text);
	
	$s = "";
	
	for ($i=0; $i<strlen($text); $i+=2)
	{
		$s .= chr(hexdec(substr($text, $i, 2)));
	}
	
	echo iconv("utf-16be", "utf-8", $s) . "\n";
	
	// Tisztelt Ügyfelünk! A(z) AAAAAAAA kóddal léphet be online önkiszolgáló felületünkre. Üdvözlettel: Telekom
?>
